import React from "react";
import Joi from "joi-browser";
import Form from "./common/form";
import { addNewBus } from "../services/busService";
import Table from "./common/table";

class Bus extends Form {
  state = {
    data: {
      licencePlate: "",
      school: "",
      sheets,
    },
    errors: {},
    busses: [
      {
        licencePlate: "AA-1234",
        school: "St. Mary's",
        sheets: 10,
        assignedChildren: 10,
        assignedDriver: "John Doe",
      },
      {
        licencePlate: "AA-1234",
        school: "St. Mary's",
        sheets: 10,
        assignedChildren: 10,
        assignedDriver: "John Doe",
      },
    ],
  };

  schema = {
    licencePlate: Joi.string().required().label("Licence Plate"),
    school: Joi.string().required().label("School"),
    sheets: Joi.number().required().label("Sheets"),
  };

  doSubmit = async () => {
    try {
      const { data } = this.state;
      await addNewBus({
        licencePlateNumber: data.licencePlate,
        school: data.school,
        sheets: data.sheets,
        assignedClildren: 0,
        assignedDriver: "",
      });
      // Optionally, you can redirect the user or perform other actions after the bus is successfully added.
      console.log("Bus added successfully!");
    } catch (error) {
      console.error("Error adding bus:", error.message);
    }
  };

  render({ busses }) {
    return (
      <div className="row">
        <div className="col-4">
          <h1>Add New Bus</h1>
          <form onSubmit={this.handleSubmit}>
            {this.renderInput("licencePlate", "Licence Plate")}
            {this.renderInput("school", "School")}
            {this.renderInput("sheets", "Sheets")}
            {this.renderButton("Save")}
          </form>
        </div>
        <div className="col">
          <Table
            columns={[
              { path: "licencePlate", label: "Licence Plate" },
              { path: "school", label: "School" },
              { path: "sheets", label: "Sheets" },
              { path: "assignedChildren", label: "Assigned Children" },
              { path: "assignedDriver", label: "Assigned Driver" },
            ]}
            data={busses}
          />
        </div>
      </div>
    );
  }
}

export default Bus;
